import React from 'react'
import {View, Button, Text, TextInput, StyleSheet, FlatList, Alert} from 'react-native'
//import {CheckBox, Icon} from 'react-native-elements'
import Constants from 'expo-constants'

const styles = StyleSheet.create({
  container : {
    flex : 1,
    paddingTop : Constants.statusBarHeight,
  },
  head : {

    alignItems : 'center',
    justifyContent :'center',
    backgroundColor : 'grey',
    height : "9%",
    borderRadius : 10,

  },
  headText : {
    fontSize : 28,
    fontWeight : 'bold',
    color : 'white',
  },
  counts : {
    padding : 20,
    flexDirection : 'row',
  },
  count1 : {
    fontSize : 17,
    fontWeight : 'bold'
  },
  count2 : {
    fontSize : 17,
    paddingLeft : "10%",
    fontWeight : 'bold',
  },
  button : {
    alignItems : 'center',
    paddingBottom : 10,
    paddingLeft : "40%"
  },
  in : {
    flexDirection : 'row',
    justifyContent: 'center',
    paddingBottom : 10,
    paddingTop : 5,
  },
  input : {
    width : 200,
    borderWidth : 1,
    borderRadius : 5,
    paddingRight : 10,
    paddingLeft : 5,
  },
  listItem : {
    paddingLeft : 5,
    flexDirection : 'row',
    borderBottomWidth : 1,
  },
  abc : {
    justifyContent :'center'
  },
  listText : {
    fontSize : 20,
    paddingBottom : 5

  },
  todoView : {
    justifyContent : 'center'    
  },
  todoNum : {
    color : 'steelblue',
    fontSize : 20,
  },
  delete : {
    flex : 1,
    alignItems : 'flex-end',
    paddingRight : 10,
    paddingTop : 5,
  },
  deleteAll : {
    alignItems : 'flex-end',
    paddingRight : "3%",
    paddingLeft : '23%'
  },
  flexing : {
    flexDirection :'row',

  },
  flatList : {
    marginBottom : "22%"
  }
})
let id = 14
export default class App extends React.Component {
  state ={
    todoList : [],
    yetTodoCount : 0,
    addNew : false,
    newTodo : '',
    add : true,
  }

  handlePress = () => {
    this.setState(prevState => {
      return{
        addNew : !prevState.addNew
      }
    })
  }

  handleNew = () => {
    this.setState(prevState => {
      return{
        todoList : [...prevState.todoList,{Title : this.state.newTodo, id : ++id, checked : false}],
        addNew : false,
        newTodo : '',
        add : true,
        yetTodoCount : prevState.yetTodoCount + 1
      }
    })
  }
  handleCheck(id,checked){
    this.setState(prevState => {
      return{
        todoList : prevState.todoList.map(todo => {
          if (todo.id !== id) return todo
          return {
            id: todo.id,
            Title: todo.Title,
            checked: !todo.checked,
          }
        }),
        yetTodoCount : checked ? prevState.yetTodoCount + 1 : prevState.yetTodoCount - 1
      }
    })
  }

  handleDelete(id,checked){
    this.setState(prevState => {
      return{
        todoList : prevState.todoList.filter(todo => todo.id !== id),
        yetTodoCount :  checked ? prevState.yetTodoCount : prevState.yetTodoCount - 1
      }
    })
  }

  handleDeleteAll = () => {
    Alert.alert(
      "Delete All",
      "Click confirm to delete all To-do!",
      [
        {
          text : 'CANCEL',
          onPress : () => {} 
        },
        {
          text : 'CONFIRM',
          onPress : () => this.setState({todoList : [], yetTodoCount : 0})
        }
      ]
    )
  }

  todoList = (item,index) => {
    return(
      <View style ={styles.listItem}>
      <View style = {styles.todoView}><Text style = {styles.todoNum}>{index + 1}) </Text></View>
        <CheckBox
          onPress = {() => this.handleCheck(item.id,item.checked)}
          center
          checked = {item.checked}
        />
        <View style = {styles.abc}><Text style = {styles.listText}>{item.Title}</Text></View> 
        <View style = {styles.delete}>
          <Icon color = 'red' name = 'delete' size = {35} onPress={()=>this.handleDelete(item.id,item.checked)} />
        </View>
      </View>
    )
  }

  render() {
    return(
      <View style = {styles.container}>
        <View style = {styles.head}>
          <Text style = {styles.headText}> To-Do Tasks </Text>
        </View>
        <View style = {styles.counts}>
          <Text style = {styles.count1}>Total To-Do Count : {this.state.todoList.length}</Text>
          <Text style = {styles.count2}>Yet To-do Count : {this.state.yetTodoCount}</Text>
        </View>
        <View style = {{borderBottomWidth : 2}}>
        <View style = {styles.flexing}>
        <View style = {styles.button}>
          <Button  title = "Add To-Do" onPress = {this.handlePress}/>
        </View>
        <View style = {styles.deleteAll}>
        {(this.state.todoList.length) ?
          <Icon name = 'delete-forever' color = 'crimson' size = {40}
          onPress = {this.handleDeleteAll} /> : null }
        </View> 
        </View>
        {this.state.addNew && 
        <View style = {styles.in}>
          <TextInput 
            style = {styles.input}
            placeholder = "Enter new To-Do...  "
            onChangeText = {(text) => this.setState({newTodo : text, add : false})}
            value = {this.state.newTodo}
          />
          <Button title = 'Add' onPress = {this.handleNew} disabled = {this.state.add}/>
        </View>}
        </View>
        <View>
            <FlatList 
              inverted = {false}
              style = {styles.flatList}
              data = {this.state.todoList}
              renderItem = {({item,index}) => this.todoList(item,index)}
              keyExtractor = {item => `${item.id}`}
            />
        </View>
      </View>

    )
  }
}
